package cafe.nekohouse.nekoapi.plugins

import cafe.nekohouse.nekoapi.PoemSize
import cafe.nekohouse.nekoapi.getPoem
import io.ktor.http.*
import io.ktor.serialization.kotlinx.json.*
import io.ktor.server.application.*
import io.ktor.server.plugins.contentnegotiation.*
import io.ktor.server.request.*
import io.ktor.server.response.*
import io.ktor.server.routing.*
import java.util.*

fun Application.configureAiPoem() {
    install(ContentNegotiation) {
        json()
    }
    routing {
        post("/aipoem") {

            val poem = getPoem(call.receiveText())
            call.respond(
                mapOf(
                    "short" to poem.getPoem(PoemSize.Short),
                    "middle" to poem.getPoem(PoemSize.Middle),
                    "long" to poem.getPoem(PoemSize.Long)
                )
            )
        }
        get("/aipoem") {
            try {
                val text = call.parameters["text"] ?: ""
                val format = call.parameters["format"] ?: "text"
                val length = call.parameters["length"] ?: "Short"
                val pic64 =
                    """"""
                val poem = getPoem(pic64, text)
                if (format.lowercase(Locale.getDefault()) == "text")
                    return@get call.respondText { poem.getPoem(PoemSize.valueOf(length.titled())) }
                call.respond(
                    mapOf(
                        "short" to poem.getPoem(PoemSize.Short),
                        "middle" to poem.getPoem(PoemSize.Middle),
                        "long" to poem.getPoem(PoemSize.Long)
                    )
                )
            } catch (error: Exception) {
                call.respondText { "Error内部错误" }
            }
        }
    }
}

fun String.titled(): String {
    return this.substring(0, 1).uppercase(Locale.getDefault()) + this.substring(1)
}